attribute vec2 s_Five;
varying vec2 textureCoordinates;
uniform vec4 myOffset;

void main()
{
	// this is the usual transform
	// gl_Position = gl_ModelViewProjectionMatrix * gl_Vertex;
	// but this pulls out a square from 0->1 to fill the screen
	gl_Position = (gl_Vertex-vec4(0.5+myOffset.z/4.0, 0.5+myOffset.w/4.0,0,0))*vec4(2.0+myOffset.x-0.5,-2.0+myOffset.y-0.5,2,1);
	textureCoordinates = s_Five;
}

uniform sampler2DRect theTexture;
varying vec2 textureCoordinates;

void main()
{
	//the OpenCV library presents its color information in a bizzare order
	// 'grab' isn't a verb here, it means green, red, alpha, blue
	// we have to 'swizzle' the order of the colors we get out of the PImage into 
	// something that OpenGL likes

	gl_FragColor = texture2DRect(theTexture, textureCoordinates.xy).grab;


	gl_FragColor = texture2DRect(theTexture, gl_FragColor.rb*vec2(150,150)).rrrr;
	gl_FragColor.rgb = (gl_FragColor.rgb-vec3(0.5, 0.5, 0.5))*10.0;

	gl_FragColor.a = 0.04;

}
